import { useState } from 'react';
import { Clock, Bell } from 'lucide-react';

export default function RemindersNotifications() {
  // Data for reminders section
  const reminderItems = [
    { id: 1, text: 'Medicine' },
    { id: 2, text: 'Doctor appointment' },
    { id: 3, text: 'Bedtime' },
    { id: 4, text: 'Exercise' },
    { id: 5, text: 'Medication' },
    { id: 6, text: 'Meals' },
    { id: 7, text: 'Drink water' },
    { id: 8, text: 'Monthly payments' },
  ];

  // Data for notifications section
  const notificationItems = [
    { id: 1, text: 'Medicine', enabled: true },
    { id: 2, text: 'Drink water', enabled: true },
    { id: 3, text: 'Pension status update', enabled: true },
    { id: 4, text: 'Bank visit', enabled: true },
    { id: 5, text: 'Bill payments', enabled: true },
    { id: 6, text: 'Eye check-up', enabled: true },
    { id: 7, text: 'Booking confirmation', enabled: false },
  ];

  // State to track which notifications are enabled
  const [notifications, setNotifications] = useState(notificationItems);

  // Toggle notification state
  const toggleNotification = (id) => {
    setNotifications(
      notifications.map((item) =>
        item.id === id ? { ...item, enabled: !item.enabled } : item
      )
    );
  };

  return (
    <div className="flex flex-col bg-white min-h-screen w-full mx-auto">
      <div className="text-center py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-12">Reminders & Notifications</h1>

        {/* Reminders Section */}
        <div className="flex flex-col items-center mb-16">
          <div className="flex items-center mb-8 text-3xl font-bold">
            <Clock size={32} className="mr-2" />
            <span>Reminders</span>
          </div>

          <div className="flex flex-col space-y-6 w-full px-4 max-w-md">
            {reminderItems.map((item) => (
              <div key={item.id} className="flex justify-between items-center w-full">
                <span className="text-lg text-gray-800">{item.text}</span>
                <Clock size={24} className="text-gray-600" />
              </div>
            ))}
          </div>
        </div>

        {/* Notifications Section */}
        <div className="flex flex-col items-center mb-12">
          <div className="flex items-center mb-8 text-3xl font-bold">
            <Bell size={32} className="mr-2" />
            <span>Notifications</span>
          </div>

          <div className="flex flex-col space-y-12 w-full px-4 max-w-md">
            {notifications.map((item) => (
              <div key={item.id} className="flex justify-between items-center w-full">
                <span className="text-lg text-gray-800">{item.text}</span>
                <button 
                  className={`w-12 h-6 rounded-full flex items-center transition-colors duration-200 ease-in-out ${
                    item.enabled ? 'bg-orange-400 justify-end' : 'bg-gray-300 justify-start'
                  }`}
                  onClick={() => toggleNotification(item.id)}
                >
                  <span className="w-5 h-5 bg-white rounded-full mx-0.5 shadow-md"></span>
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
