// src/components/Features/Features.js
import React from 'react';

const Features = () => {
  return (
    <section className="p-8 bg-gray-100 text-center">
      <h3 className="text-2xl font-semibold mb-6">How LinkClick Helps</h3>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {['Audio Guides', 'Reminders', 'Access Services'].map((title, i) => (
          <div key={i} className="bg-white p-6 rounded shadow hover:shadow-lg transition">
            <h4 className="text-lg font-semibold">{title}</h4>
            <p className="text-sm text-gray-600 mt-2">
              {title === 'Audio Guides'
                ? 'Voice instructions to help navigate.'
                : title === 'Reminders'
                ? 'Get notified about appointments and bills.'
                : 'Easily reach essential services.'}
            </p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Features;
