import { useState } from 'react';
import { Clock, Bell } from 'lucide-react';

export default function RemindersNotifications() {
  // Data for reminders section
  const reminderItems = [
    { id: 1, text: 'Medicine' },
    { id: 2, text: 'Doctor appointment' },
    { id: 3, text: 'Bedtime' },
    { id: 4, text: 'Exercise' },
    { id: 5, text: 'Medication' },
    { id: 6, text: 'Meals' },
    { id: 7, text: 'Drink water' },
    { id: 8, text: 'Monthly payments' },
  ];

  // Data for notifications section
  const notificationItems = [
    { id: 1, text: 'Medicine', enabled: false },
    { id: 2, text: 'Drink water', enabled: false },
    { id: 3, text: 'Pension status update', enabled: false },
    { id: 4, text: 'Bank visit', enabled: false },
    { id: 5, text: 'Bill payments', enabled: false },
    { id: 6, text: 'Eye check-up', enabled: false },
    { id: 7, text: 'Booking confirmation', enabled: false },
  ];

  // State to track which notifications are enabled
  const [notifications, setNotifications] = useState(notificationItems);

  // Toggle notification state
  const toggleNotification = (id) => {
    setNotifications(
      notifications.map((item) =>
        item.id === id ? { ...item, enabled: !item.enabled } : item
      )
    );
  };

  return (
    <div className="flex flex-col bg-white h-screen px-4 py-8 max-w-lg mx-auto">
      {/* Header with illustration */}
      <div className="flex items-center justify-between mb-16">
        <div className="w-24">
          <img 
            src="/api/placeholder/120/120" 
            alt="Notification illustration" 
            className="w-full" 
          />
        </div>
        <h1 className="text-3xl font-bold text-gray-800 flex-1 ml-2">
          Reminders & Notifications
        </h1>
      </div>

      {/* Reminders Section */}
      <div className="mb-12">
        <h2 className="text-2xl font-semibold text-gray-700 flex items-center mb-6">
          <Clock className="mr-2" size={24} />
          Reminders
        </h2>

        <div className="flex flex-col space-y-4">
          {reminderItems.map((item) => (
            <div key={item.id} className="flex items-center justify-between border-b border-gray-200 pb-3">
              <span className="text-gray-700">{item.text}</span>
              <div className="w-6 h-6 rounded-full bg-gray-200 flex items-center justify-center">
                <Clock size={14} className="text-gray-500" />
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Notifications Section */}
      <div>
        <h2 className="text-2xl font-semibold text-gray-700 flex items-center mb-6">
          <Bell className="mr-2" size={24} />
          Notifications
        </h2>

        <div className="flex flex-col space-y-4">
          {notifications.map((item) => (
            <div key={item.id} className="flex items-center justify-between border-b border-gray-200 pb-3">
              <span className="text-gray-700">{item.text}</span>
              <button 
                className={`w-12 h-6 rounded-full flex items-center transition-colors duration-200 ease-in-out ${
                  item.enabled ? 'bg-blue-500 justify-end' : 'bg-gray-300 justify-start'
                }`}
                onClick={() => toggleNotification(item.id)}
              >
                <span className="w-5 h-5 bg-white rounded-full mx-0.5 shadow-md"></span>
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
