import React, { useState } from 'react';
import './Reminders.css';
import { Info, Bell, Clock } from 'lucide-react';

const reminders = [
  "Medicine",
  "Doctor appointment",
  "Bedtime",
  "Exercise",
  "Medication",
  "Meals",
  "Drink water",
  "Monthly payments",
];

const initialNotifications = [
  { name: "Medicine", enabled: true },
  { name: "Drink water", enabled: true },
  { name: "Pension status update", enabled: false },
  { name: "Bank visit", enabled: true },
  { name: "Bill payments", enabled: true },
  { name: "Eye check-up", enabled: false },
  { name: "Booking confirmation", enabled: true },
];

export default function Reminders() {
  const [notifications, setNotifications] = useState(initialNotifications);

  const handleToggle = (index) => {
    const updated = [...notifications];
    updated[index].enabled = !updated[index].enabled;
    setNotifications(updated);
  };

  return (
    <div className="container">
      <div className="header">
        <img
          src="/icons/notifications-illustration.png"
          alt="Notifications Illustration"
        />
        <h1>Reminders & Notifications</h1>
      </div>

      <div className="card">
        <div className="card-header">
          <Clock className="icon" />
          <span>Reminders</span>
        </div>
        <ul className="item-list">
          {reminders.map((item, index) => (
            <li key={index} className="item">
              <span>{item}</span>
              <Info className="icon info-icon" />
            </li>
          ))}
        </ul>
      </div>

      <div className="card">
        <div className="card-header">
          <Bell className="icon" />
          <span>Notifications</span>
        </div>
        <ul className="item-list">
          {notifications.map((item, index) => (
            <li key={index} className="item">
              <span>{item.name}</span>
              <label className="toggle-switch">
                <input
                  type="checkbox"
                  checked={item.enabled}
                  onChange={() => handleToggle(index)}
                />
                <span className="slider" />
              </label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
