import { useState } from 'react';
import { Clock, Bell, Info } from 'lucide-react';

export default function RemindersNotifications() {
  const [notificationStates, setNotificationStates] = useState({
    medicine: false,
    drinkWater: false,
    pensionStatus: false,
    bankVisit: false,
    billPayments: false,
    eyeCheckup: false,
    bookingConfirmation: true
  });

  const toggleNotification = (key) => {
    setNotificationStates(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
  };

  const reminderItems = [
    { text: "Medicine" },
    { text: "Doctor appointment" },
    { text: "Bedtime" },
    { text: "Exercise" },
    { text: "Medication" },
    { text: "Meals" },
    { text: "Drink water" },
    { text: "Monthly payments" }
  ];

  const notificationItems = [
    { text: "Medicine", key: "medicine" },
    { text: "Drink water", key: "drinkWater" },
    { text: "Pension status update", key: "pensionStatus" },
    { text: "Bank visit", key: "bankVisit" },
    { text: "Bill payments", key: "billPayments" },
    { text: "Eye check-up", key: "eyeCheckup" },
    { text: "Booking confirmation", key: "bookingConfirmation" }
  ];

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow">
      <div className="flex items-center mb-8">
        <div className="w-20 h-20 mr-4">
          <img src="4966443.jpg" alt="Notifications icon" className="w-full h-full" />
        </div>
        <h1 className="text-3xl font-bold text-gray-800">Reminders & Notifications</h1>
      </div>

      <div className="mb-10">
        <div className="flex items-center mb-6">
          <Clock className="text-gray-600 mr-2" size={24} />
          <h2 className="text-2xl font-medium text-gray-800">Reminders</h2>
        </div>

        <ul className="space-y-4">
          {reminderItems.map((item, index) => (
            <li key={index} className="flex justify-between items-center">
              <span className="text-gray-700">{item.text}</span>
              <div className="flex items-center">
                <button className="w-6 h-6 rounded-full bg-gray-200 flex items-center justify-center">
                  <Info size={16} className="text-gray-600" />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <div className="flex items-center mb-6">
          <Bell className="text-gray-600 mr-2" size={24} />
          <h2 className="text-2xl font-medium text-gray-800">Notifications</h2>
        </div>

        <ul className="space-y-4">
          {notificationItems.map((item) => (
            <li key={item.key} className="flex justify-between items-center">
              <span className="text-gray-700">{item.text}</span>
              <div>
                <button 
                  className={`w-12 h-6 rounded-full flex items-center relative ${notificationStates[item.key] ? 'bg-blue-500' : 'bg-gray-300'}`}
                  onClick={() => toggleNotification(item.key)}
                >
                  <span 
                    className={`w-5 h-5 rounded-full bg-white absolute transition-all ${notificationStates[item.key] ? 'left-6' : 'left-1'}`}
                  />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
