// src/components/Reminders/Reminders.js
import React, { useState } from 'react';
import {
  FaPills,
  FaTint,
  FaMoneyCheckAlt,
  FaUniversity,
  FaFileInvoiceDollar,
  FaEye,
  FaCheckCircle
} from 'react-icons/fa';
import './Reminders.css';

const Reminders = () => {
  const [notifications, setNotifications] = useState({
    medicine: true,
    drinkWater: true,
    pensionStatus: false,
    bankVisit: false,
    billPayments: true,
    eyeCheckUp: false,
    bookingConfirmation: true,
  });

  const handleToggle = (key) => {
    setNotifications((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  const reminderIcons = {
    medicine: <FaPills />,
    drinkWater: <FaTint />,
    pensionStatus: <FaMoneyCheckAlt />,
    bankVisit: <FaUniversity />,
    billPayments: <FaFileInvoiceDollar />,
    eyeCheckUp: <FaEye />,
    bookingConfirmation: <FaCheckCircle />,
  };

  return (
    <div className="app">
      <h1>Reminders & Notifications</h1>

      <div className="section">
        <h2>Reminders</h2>
        <ul>
          {Object.keys(reminderIcons).map((reminder) => (
            <li key={reminder}>
              <span className="reminder-icon">{reminderIcons[reminder]}</span>
              {reminder.replace(/([A-Z])/g, ' $1')}
            </li>
          ))}
        </ul>
      </div>

      <div className="section">
        <h2>Notifications</h2>
        <ul>
          {Object.entries(notifications).map(([key, value]) => (
            <li key={key}>
              {key.replace(/([A-Z])/g, ' ')}
              <label className="switch">
                <input type="checkbox" checked={value} onChange={() => handleToggle(key)} />
                <span className="slider round"></span>
              </label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Reminders;
