// src/components/Reminders/Reminders.js
import React, { useState } from 'react';
import './Reminders.css'; // Ensure this CSS file exists for styling

const Reminders = () => {
  const [notifications, setNotifications] = useState({
    medicine: true,
    drinkWater: true,
    pensionStatus: false,
    bankVisit: false,
    billPayments: true,
    eyeCheckUp: false,
    bookingConfirmation: true,
  });

  const handleToggle = (key) => {
    setNotifications((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  // URL for icons (You can replace these URLs with actual image links)
  const reminderIcons = {
    medicine: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    doctorAppointment: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    bedtime: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    exercise: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    medication: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    meals: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    drinkWater: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
    monthlyPayments: 'https://cdn-icons-png.freepik.com/256/763/763449.png?uid=R143236635&ga=GA1.1.1361193218.1746250785&semt=ais_hybrid',
  };

  return (
    <div className="app">
      <h1>Reminders & Notifications</h1>

      <div className="section">
        <h2>Reminders</h2>
        <ul>
          {Object.keys(reminderIcons).map((reminder) => (
            <li key={reminder}>
              <img src={reminderIcons[reminder]} alt={`${reminder} icon`} className="reminder-icon" />
              {reminder.replace(/([A-Z])/g, ' $1')}
            </li>
          ))}
        </ul>
      </div>

      <div className="section">
        <h2>Notifications</h2>
        <ul>
          {Object.entries(notifications).map(([key, value]) => (
            <li key={key}>
              {key.replace(/([A-Z])/g, ' ')} 
              <label className="switch">
                <input type="checkbox" checked={value} onChange={() => handleToggle(key)} />
                <span className="slider round"></span>
              </label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Reminders;
